let pokemonRepository = (function() {
  let t = [],
    e = 'https://pokeapi.co/api/v2/pokemon/?limit=150';
  return {
    getAll: function() {
      return t;
    },
    addListItem: function(t) {
      let e = document.querySelector('.pokemon-list'),
        n = document.createElement('li');
      n.classList.add('list-group-item');
      let o = document.createElement('button');
      (o.innerText = t.name),
        o.classList.add('btn'),
        o.classList.add('btn-warning'),
        o.setAttribute('data-toggle', 'modal'),
        o.setAttribute('data-target', '#pokemon-modal'),
        n.appendChild(o),
        e.appendChild(n),
        o.addEventListener('click', function() {
          !(function(t) {
            (function(t) {
              let e = t.detailsURL;
              return fetch(e)
                .then(function(t) {
                  return t.json();
                })
                .then(function(e) {
                  (t.imageURL = e.sprites.front_default), (t.height = e.height);
                })
                .catch(function(t) {
                  console.error(t);
                });
            })(t).then(function() {
              !(function(t) {
                let e = document.querySelector('.modal-body'),
                  n = document.querySelector('.modal-title');
                (n.innerHTML = ''), (e.innerHTML = '');
                let o = document.createElement('h1');
                o.innerText = t.name;
                let i = document.createElement('img');
                i.classList.add('modal-img'), i.setAttribute('src', t.imageURL);
                let c = document.createElement('p');
                (c.innerText = 'height: ' + t.height),
                  n.appendChild(o),
                  e.appendChild(i),
                  e.appendChild(c);
              })(t);
            });
          })(t);
        });
    },
    loadList: function() {
      return fetch(e)
        .then(function(t) {
          return t.json();
        })
        .then(function(e) {
          e.results.forEach(function(e) {
            !(function(e) {
              t.push(e);
            })({ name: e.name, detailsURL: e.url });
          });
        })
        .catch(function(t) {
          console.error(t);
        });
    }
  };
})();
pokemonRepository.loadList().then(function() {
  pokemonRepository.getAll().forEach(function(t) {
    pokemonRepository.addListItem(t);
  });
});
